let confirmed = document.getElementById("confirmed");
let deaths = document.getElementById("deaths");
let active = document.getElementById("active");
let recovered = document.getElementById("recovered");

function run_ajax(){
    return $.ajax({
        url: 'https://covid19.mathdro.id/api/countries/Bangladesh',
        type: 'GET',
        async: false,
    });
}

async function set_values() {
    let data = await run_ajax();
    confirmed.setAttribute("data-to", data.confirmed.value);
    deaths.setAttribute("data-to", data.deaths.value);
    recovered.setAttribute("data-to", data.recovered.value);
    active.setAttribute("data-to", data.confirmed.value - data.recovered.value - data.deaths.value);
}

set_values();